    @ViewBuilder
    private func milestonesView() -> some View {
        let totalDistance = store.activities.reduce(0.0) { $0 + $1.distanceMeters }
        let longestRun = store.activities.map { $0.distanceMeters }.max() ?? 0
        VStack(alignment: .leading, spacing: 8) {
