                    }
                }

                Section("Leaderboard") {
                    ForEach(community.leaderboard) { e in
                        HStack {
                            Text("#\(e.rank)")
                                .foregroundColor(.taqvoAccentText)
                                .frame(width: 32)
                            Text(e.userName)
                                .foregroundColor(.taqvoTextDark)
                            Spacer()
                            Text(String(format: "%.1f km", e.totalDistanceMeters/1000.0))
                                .foregroundColor(.taqvoTextDark)
                                .font(.headline)
                        }
                        .padding(.vertical, 4)
                        .listRowBackground(Color.black.opacity(0.08))
                    }
                }
