            .listStyle(.grouped)
            .background(Color.taqvoBackgroundDark)
            .navigationTitle("Community")
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button {
                        community.load()
                        community.refreshProgress(from: store)
                    } label: {
                        Label("Reload", systemImage: "arrow.clockwise")
